/* Libraries */
import styled from 'styled-components';

const Source = styled.p `
  /* Style */
  color: Black;
  font-family: var(--main-font-light);
  font-size: 1.8rem;
  margin-bottom: 1rem;
  text-align: left;
  -webkit-letter-spacing: 0;
  -moz-letter-spacing: 0;
  -ms-letter-spacing: 0;
  letter-spacing: 0;
  font-style: italic;

`;

export default Source;
