import React, { useEffect, useState } from 'react'
import styled from 'styled-components';
import { Helmet } from 'react-helmet'
import ApiController from '../api/SkyAlertAPI';
import Navbarblack from '../components/navbarblack'
import Cardblog from '../components/cardblog'
import Footer from '../components/footer'
import Copyright from '../components/copyright'
import './blog.css';
import { device } from '../utils/media';
import Pagination from "react-js-pagination";
import MediaImage from '../utils/mediaImage';

const Api = new ApiController();

const Content = styled.div`
  margin : 3rem;
  .paginated{
    width:100%;
    padding: 0rem 4rem 2rem;
    @media ${device.laptop} {
      padding: 3rem 3rem 2rem;
      margin-bottom:5vh;
      margin-top:5vh;
      width:50%;
      // max-width: 300px;
      //  overflow-x: scroll;
    }
    @media ${device.tablet} {
      padding: 3rem 3rem 2rem;
      margin-bottom:5vh;
      margin-top:5vh;
      width:50%;
      // max-width: 300px;
      // overflow-x: scroll;
    }
    li{
      background: white;
      display: inline;
      padding: 10px 15px;
      color: black;
      -webkit-text-decoration: none;
      text-decoration: none;
      list-style: none;
      margin: .5rem;


      a{
        text-decoration: none;
        color: black;
        font-size: 1.5rem;

      }
    }
    li.active{
      background: #f47d55;
    }
    li.active a{
      color:white !important;
    }
  }

  .totalPost{
    width:100%;
    padding: 0rem 3rem 2rem;
    @media ${device.laptop} {
      width:50%;
      padding: 3rem 3rem 2rem;
      margin-bottom:5vh;
      margin-top:5vh;
    }
    @media ${device.tablet} {
      width:50%;
      padding: 3rem 3rem 2rem;
      margin-bottom:5vh;
      margin-top:5vh;
    }
  }
`;

const EPP = 6;
const Blog = (props) => {

  const [news, setNews] = useState([]);
  const [totalNews, setTotalNews] = useState([]);
  const [totalPage, setTotalPage] = useState([]);
  const [activePage, setActivePage] = useState(1);
  const [postRangeInitial, setPostRangeInitial] = useState(1);
  const [postRangeFinal, setPostRangeFinal] = useState(EPP);

  const getAllNews = async (page) => {
    try {
      const response = await Api.getFeed(page, EPP);
      setNews(response.data.articles);
      setTotalNews(response.total);
      setTotalPage(Math.ceil(20 / EPP));

    } catch (e) {
      console.error('Could not retrieve articles', e);
    }
  }

  const handlePageChange = (activePage) => {

    if (activePage) {
      setPostRangeFinal(EPP * activePage);
    }

    getAllNews(activePage);
    setActivePage(activePage);

  }

  const getMediaImage = (newsItem) => {
    const mediaImage = new MediaImage(newsItem.mainMedia.document);
    return mediaImage.getPic();
  }

  useEffect(() => {
    getAllNews(activePage);
  }, []);

  return (
    <div className="blog-container">
      <Helmet>
        <title>No te Pierdas Las Novedades Que SkyAlert Tiene Para ti</title>
        <meta
          name="description"
          content="Conoce todo lo nuevo que SkyAlert tiene para ti. Haz clic aquí para no quedarte afuera de las actualizaciones que constantemente estamos publicando."
        />
        <meta
          property="og:title"
          content="No te Pierdas Las Novedades Que SkyAlert Tiene Para ti"
        />
        <meta
          property="og:description"
          content="Conoce todo lo nuevo que SkyAlert tiene para ti. Haz clic aquí para no quedarte afuera de las actualizaciones que constantemente estamos publicando."
        />
      </Helmet>
      <Navbarblack rootClassName="navbarblack-root-class-name5"></Navbarblack>
      <div className="blog-container1">
        <span>
          Home
          <span
            dangerouslySetInnerHTML={{
              __html: ' ',
            }}
          />
        </span>
        <span className="blog-text1">
          {' '}
          /
          <span
            dangerouslySetInnerHTML={{
              __html: ' ',
            }}
          />
        </span>
        <span className="blog-text2">SkyAlert News</span>
      </div>
      <div className="blog-container2">
        <div className="blog-container3">
          <img
            alt="image"
            src="/playground_assets/skyalert-%5B2%5D_04-200w.png"
            className="blog-image img_shii"
          />
          <div className="blog-container4">
            <h2 className="blog-text3 heading2">
              <span>
                Portal de noticias
                <span
                  dangerouslySetInnerHTML={{
                    __html: ' ',
                  }}
                />
              </span>
              <span className="blog-text5">SkyAlert</span>
            </h2>
            <span className="blog-text6">
              <span
                dangerouslySetInnerHTML={{
                  __html: ' ',
                }}
              />
            </span>
          </div>
        </div>
      </div>

      <div className="blog-container5">
        {news.map((newsItem, index) => <Cardblog
          key={index}
          image_src={getMediaImage(newsItem)}
          rootClassName="cardblog-root-class-name"
          text1={newsItem.summary}
          heading={newsItem.title}
          slug={newsItem.slug}
        >
        </Cardblog>
        )}
      </div>
      <Content className='prensa-container03'>
        <div className="totalPost">
          Mostrando  <span>{postRangeInitial}</span> - <span>{postRangeFinal}</span> post en {totalNews}
        </div>
        <div className="paginated">
          <Pagination
            activePage={activePage}
            itemsCountPerPage={EPP}
            totalItemsCount={totalNews}
            pageRangeDisplayed={totalPage}
            onChange={handlePageChange}
          />
        </div>
      </Content>
      <main className="blog-main">
        <div className="blog-container7"></div>
      </main>
      <Footer rootClassName="footer-root-class-name6"></Footer>
      <Copyright></Copyright>
    </div>
  )
}

export default Blog
